mapper.update:
UPDATE t_posts 
SET read_count = read_count + 1 
WHERE post_no = #{postNo}

UPDATE t_posts 
SET 
    post_title = #{postTitle},
    post_content = #{postContent}
WHERE post_no = #{postNo}
  AND user_id = #{userId}

UPDATE t_posts 
SET post_status = #{param2} 
WHERE post_no = #{param1}
